package CollectionsKnowledgeProving;
//Array has primitive data types
public class ArrayDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] num = new int[3];
		num[0] = 40;
		num[1] = 50;
		num[2] = 60;
	    int i = 0;
		while( i <= num.length - 1) {
			
			System.out.println(num[i]);
			i++;		
		}

	}

}
